function [data limits] = AndromedaParadox(varargin)  opts = {};  opts = [opts 'name = ''Andromeda Paradox'''];
  opts = [opts 'labels = {''Andromeda''; ''Bystander''; ''Traveller''}'];  opts = [opts 'v = [0 0 0.15]'];  opts = [opts 'x = [0.2 0 -0.15/2]'];  opts = [opts 't = [-0.5 -0.5 -0.5]'];  opts = [opts 'limits = [-0.2 0.3 -0.5 0]'];  opts = [opts 'gal = false'];  opts = [opts 'los = true'];  opts = [opts 'light = false'];  opts = [opts 'nolegend = false'];  opts = [opts 'vo = 0'];  opts = [opts 'rs = 0'];  opts = [opts 'res = 0.001'];  opts = [opts 'gal = false'];  opts = [opts 'showage = false'];    opts = [opts varargin];  for i=1:numel(opts)    arg = opts{i};    if isempty(strfind(arg,'='))      arg = [arg '=true'];    endif    eval([arg ';'])  endfor  [data limits] = minkowskiChart(opts{:});  # Now over there  now = [x(1), -x(1)];  now = lorentzBoost(now, vo);  plot([limits(1) now(1)], [now(2) now(2)], 'ks-;Now over there;');endfunction