function result = trim(data, limits)  result = [];  x = data(:,1);  t = data(:,2);  inrange = find(x >= limits(1));  if isempty(inrange)    return  elseif inrange(1) > 1    intrange = [inrange(1) - 1; inrange];  endif  x = x(inrange);  t = t(inrange);  inrange = find(x <= limits(2));  if isempty(inrange)    return  elseif inrange(end) < rows(x)    intrange = [inrange; inrange(end) + 1];  endif  x = x(inrange);  t = t(inrange);  inrange = find(t >= limits(3));    if isempty(inrange)    return  elseif inrange(1) > 1    intrange = [inrange(1) - 1; inrange];  endif  x = x(inrange);  t = t(inrange);  inrange = find(t <= limits(4));    if isempty(inrange)    return  elseif inrange(end) < rows(t)    intrange = [inrange; inrange(end) + 1];  endif  x = x(inrange);  t = t(inrange);  result = cat(2, x, t);endfunction